contracts/BitReGenToken.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

/**
 * BitReGen (BRG) – BEP-20 on BNB Chain
 * Tokenomics:
 *  - 30% Presale
 *  - 20% Team (locked via VestingWallet)
 *  - 20% Ecosystem
 *  - 15% Marketing
 *  - 15% Liquidity
 */

import "@openzeppelin/contracts/token/ERC20/ERC20.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/finance/VestingWallet.sol";

contract BitReGenToken is ERC20, Ownable {
    // Percentages
    uint256 public constant PRESALE_PCT = 30;
    uint256 public constant TEAM_PCT    = 20;
    uint256 public constant ECOSYS_PCT  = 20;
    uint256 public constant MKT_PCT     = 15;
    uint256 public constant LIQ_PCT     = 15;

    // Supply
    uint256 public immutable MAX_SUPPLY;

    // Wallets
    address public immutable presaleWallet;
    address public immutable ecosystemWallet;
    address public immutable marketingWallet;
    address public immutable liquidityWallet;

    // Team vesting
    VestingWallet public teamVesting;

    event TeamVestingCreated(address vesting, address beneficiary, uint64 start, uint64 duration);

    constructor(
        uint256 _maxSupply,                // e.g. 100_000_000 * 1e18
        address _presaleWallet,
        address _ecosystemWallet,
        address _marketingWallet,
        address _liquidityWallet,
        address _teamBeneficiary,          // team multisig (beneficiary)
        uint64  _vestingStartTimestamp,    // UNIX seconds (can set in future for cliff)
        uint64  _vestingDurationSeconds    // e.g. 730 days (~24 months)
    )
        ERC20("BitReGen", "BRG")
    {
        _transferOwnership(msg.sender);
        require(_maxSupply > 0, "Supply > 0");
        require(
            _presaleWallet != address(0) &&
            _ecosystemWallet != address(0) &&
            _marketingWallet != address(0) &&
            _liquidityWallet != address(0) &&
            _teamBeneficiary != address(0),
            "Zero address"
        );

        MAX_SUPPLY = _maxSupply;

        presaleWallet   = _presaleWallet;
        ecosystemWallet = _ecosystemWallet;
        marketingWallet = _marketingWallet;
        liquidityWallet = _liquidityWallet;

        // Calculate buckets
        uint256 presaleAmt = (_maxSupply * PRESALE_PCT) / 100;
        uint256 teamAmt    = (_maxSupply * TEAM_PCT)    / 100;
        uint256 ecosysAmt  = (_maxSupply * ECOSYS_PCT)  / 100;
        uint256 mktAmt     = (_maxSupply * MKT_PCT)     / 100;
        uint256 liqAmt     = (_maxSupply * LIQ_PCT)     / 100;

        // Mint to wallets
        _mint(presaleWallet, presaleAmt);
        _mint(ecosystemWallet, ecosysAmt);
        _mint(marketingWallet, mktAmt);
        _mint(liquidityWallet, liqAmt);

        // Create vesting wallet for Team and mint there
        teamVesting = new VestingWallet(
            _teamBeneficiary,
            _vestingStartTimestamp,
            _vestingDurationSeconds
        );
        emit TeamVestingCreated(address(teamVesting), _teamBeneficiary, _vestingStartTimestamp, _vestingDurationSeconds);

        _mint(address(teamVesting), teamAmt);

        require(totalSupply() == MAX_SUPPLY, "Mint mismatch");
    }
}

contracts/PaymentGateway.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "./BitReGenToken.sol";
import "@openzeppelin/contracts/security/ReentrancyGuard.sol";
import "@openzeppelin/contracts/access/Ownable.sol";

contract BRGPaymentGateway is ReentrancyGuard, Ownable {
    BitReGenToken public immutable brgToken;

    // Payment gateway fee (in basis points, e.g., 200 = 2%)
    uint256 public processingFee = 200; // 2%
    address public feeRecipient;

    struct Payment {
        address merchant;
        address customer;
        uint256 amount;
        uint256 fee;
        string orderId;
        uint256 timestamp;
        bool completed;
    }

    mapping(string => Payment) public payments;
    mapping(address => bool) public verifiedMerchants;
    mapping(address => uint256) public merchantEarnings;

    event PaymentProcessed(
        string indexed orderId,
        address indexed merchant,
        address indexed customer,
        uint256 amount,
        uint256 fee
    );

    event MerchantVerified(address indexed merchant);
    event FeeUpdated(uint256 newFee);

    constructor(address _brgToken, address _feeRecipient) {
        brgToken = BitReGenToken(_brgToken);
        feeRecipient = _feeRecipient;
        _transferOwnership(msg.sender);
    }

    function processPayment(
        string calldata orderId,
        address merchant,
        uint256 amount
    ) external nonReentrant {
        require(verifiedMerchants[merchant], "Merchant not verified");
        require(payments[orderId].merchant == address(0), "Order already exists");
        require(amount > 0, "Amount must be > 0");

        uint256 fee = (amount * processingFee) / 10000;
        uint256 merchantAmount = amount - fee;

        // Transfer BRG from customer to contract
        require(brgToken.transferFrom(msg.sender, address(this), amount), "Transfer failed");

        // Transfer merchant amount to merchant
        require(brgToken.transfer(merchant, merchantAmount), "Merchant transfer failed");

        // Transfer fee to fee recipient
        if (fee > 0) {
            require(brgToken.transfer(feeRecipient, fee), "Fee transfer failed");
        }

        // Record payment
        payments[orderId] = Payment({
            merchant: merchant,
            customer: msg.sender,
            amount: amount,
            fee: fee,
            orderId: orderId,
            timestamp: block.timestamp,
            completed: true
        });

        merchantEarnings[merchant] += merchantAmount;

        emit PaymentProcessed(orderId, merchant, msg.sender, amount, fee);
    }

    function verifyMerchant(address merchant) external onlyOwner {
        verifiedMerchants[merchant] = true;
        emit MerchantVerified(merchant);
    }

    function updateProcessingFee(uint256 newFee) external onlyOwner {
        require(newFee <= 1000, "Fee too high"); // Max 10%
        processingFee = newFee;
        emit FeeUpdated(newFee);
    }

    function updateFeeRecipient(address newRecipient) external onlyOwner {
        require(newRecipient != address(0), "Invalid address");
        feeRecipient = newRecipient;
    }

    function getPayment(string calldata orderId) external view returns (Payment memory) {
        return payments[orderId];
    }

    function getMerchantEarnings(address merchant) external view returns (uint256) {
        return merchantEarnings[merchant];
    }
}

